contracts/AstarReceiver.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.10;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Create2.sol";
import "./interfaces/IOFTReceiverV2.sol";
import "./interfaces/IOFTV2.sol";
import "./interfaces/IOFTWithFee.sol";
import "./interfaces/XCM.sol";
import "./interfaces/XCM_v2.sol";
import "./interfaces/Types.sol";
import "./utils/BuildCallData.sol";
import "./utils/AddressToAccount.sol";
import "./AstarSlpx.sol";
import "./DerivativeContract.sol";

contract AstarReceiver is Ownable, IOFTReceiverV2 {
    bytes1 private constant ASTAR_CHAIN_TYPE = 0x00;
    bytes2 private constant ASTR_CURRENCY_ID = 0x0803;
    bytes2 private constant VASTR_CURRENCY_ID = 0x0903;
    uint256 private constant BIFROST_PARA_ID = 2030;
    bool private constant IS_RELAY_CHAIN = false;
    uint16 public constant destChainId = 257;
    address public constant BNC = 0xfFffFffF00000000000000010000000000000007;
    address public constant VASTR = 0xfffFffff00000000000000010000000000000010;
    address public constant astarSlpx =
        0xc6bf0C5C78686f1D0E2E54b97D6de6e2cEFAe9fD;
    address public constant polkadotXcm =
        0x0000000000000000000000000000000000005004;
    address public constant astrNativeOFT =
        0xdf41220C7e322bFEF933D85D01821ad277f90172;
    address public constant vAstrProxyOFT =
        0xba273b7Fa296614019c71Dcc54DCa6C922A93BcF;
    address public astarZkSlpx;
    uint256 public astrLayerZeroFee;
    uint256 public vastrLayerZeroFee;
    address public scriptTrigger;
    mapping(address => address) public callerToDerivativeAddress;
    mapping(address => bool) public isDerivativeAddress;

    event Mint(
        address indexed caller,
        address indexed derivativeAddress,
        uint256 indexed amount
    );
    event Redeem(
        address indexed caller,
        address indexed derivativeAddress,
        uint256 indexed amount
    );
    event SetDerivativeAddress(
        address indexed caller,
        address indexed derivativeAddress
    );
    event Receive(address indexed sender, uint256 indexed amount);
    event SetLayerZeroFee(
        address indexed scriptTrigger,
        uint256 indexed astrFee,
        uint256 indexed vastrFee
    );
    event SetScriptTrigger(address indexed scriptTrigger);

    constructor(address _astarZkSlpx) {
        require(_astarZkSlpx != address(0), "Invalid astarZkSlpx");
        astarZkSlpx = _astarZkSlpx;
    }

    function zkSlpxMint(address _from, address _to, uint256 _amount) internal {
        require(_from != address(0), "Invalid from");
        require(_to != address(0), "Invalid to");
        require(_amount != 0, "Invalid amount");
        xcmTransferNativeAsset(_from, _amount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN_TYPE, _to);
        bytes memory callData = BuildCallData.buildMintCallBytes(
            _from,
            ASTR_CURRENCY_ID,
            targetChain,
            "AstarZkEvm"
        );
        (uint64 transactWeight, uint256 feeAmount) = AstarSlpx(astarSlpx)
            .operationToFeeInfo(AstarSlpx.Operation.Mint);

        // xcm transact
        require(
            XCM(polkadotXcm).remote_transact(
                BIFROST_PARA_ID,
                IS_RELAY_CHAIN,
                BNC,
                feeAmount,
                callData,
                transactWeight
            ),
            "Failed to send xcm"
        );
        emit Mint(_from, _to, _amount);
    }

    function zkSlpxRedeem(
        address _from,
        address _to,
        uint256 _amount
    ) internal {
        require(_from != address(0), "Invalid from");
        require(_to != address(0), "Invalid to");
        require(_amount != 0, "Invalid amount");
        xcmTransferAsset(VASTR, _from, _amount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN_TYPE, _to);
        bytes memory callData = BuildCallData.buildRedeemCallBytes(
            _from,
            VASTR_CURRENCY_ID,
            targetChain
        );
        (uint64 transactWeight, uint256 feeAmount) = AstarSlpx(astarSlpx)
            .operationToFeeInfo(AstarSlpx.Operation.Redeem);

        // xcm transact
        require(
            XCM(polkadotXcm).remote_transact(
                BIFROST_PARA_ID,
                IS_RELAY_CHAIN,
                BNC,
                feeAmount,
                callData,
                transactWeight
            ),
            "Failed to send xcm"
        );
        emit Redeem(_from, _to, _amount);
    }

    function onOFTReceived(
        uint16 _srcChainId,
        bytes calldata,
        uint64,
        bytes32 _from,
        uint _amount,
        bytes calldata _payload
    ) external override {
        require(_srcChainId == destChainId, "only receive msg from astar-zk");
        require(
            _msgSender() == astrNativeOFT || _msgSender() == vAstrProxyOFT,
            "only native oft can call"
        );
        require(
            address(uint160(uint(_from))) == astarZkSlpx,
            "only receive msg from astarZkSlpx"
        );
        (address caller, Types.Operation operation) = abi.decode(
            _payload,
            (address, Types.Operation)
        );
        if (callerToDerivativeAddress[caller] == address(0)) {
            setDerivativeAddress(caller);
        }

        if (operation == Types.Operation.Mint) {
            IOFTWithFee(astrNativeOFT).withdraw(_amount);
            (bool success, ) = scriptTrigger.call{value: astrLayerZeroFee}("");
            require(success, "failed to charge");
            zkSlpxMint(
                caller,
                callerToDerivativeAddress[caller],
                _amount - astrLayerZeroFee
            );
        } else if (operation == Types.Operation.Redeem) {
            bool success = IERC20(VASTR).transfer(
                scriptTrigger,
                vastrLayerZeroFee
            );
            require(success, "failed to charge");
            zkSlpxRedeem(
                caller,
                callerToDerivativeAddress[caller],
                _amount - vastrLayerZeroFee
            );
        }
    }

    function claimVAstr(
        address addr,
        bytes calldata _adapterParams
    ) external payable {
        require(_msgSender() == scriptTrigger, "must be scriptTrigger");
        address derivativeAddress = callerToDerivativeAddress[addr];
        require(derivativeAddress != address(0), "invalid address");
        uint256 amount = DerivativeContract(derivativeAddress)
            .withdrawErc20Token(VASTR);
        IERC20(VASTR).approve(vAstrProxyOFT, amount);
        ICommonOFT.LzCallParams memory callParams = ICommonOFT.LzCallParams(
            payable(_msgSender()),
            address(0),
            _adapterParams
        );
        bytes32 toAddress = bytes32(uint256(uint160(addr)));
        (uint256 estimateFee, ) = IOFTV2(vAstrProxyOFT).estimateSendFee(
            destChainId,
            toAddress,
            amount,
            false,
            _adapterParams
        );
        require(msg.value >= estimateFee, "too small fee");
        if (msg.value != estimateFee) {
            uint256 refundAmount = msg.value - estimateFee;
            (bool success, ) = _msgSender().call{value: refundAmount}("");
            require(success, "failed to refund");
        }
        IOFTV2(vAstrProxyOFT).sendFrom{value: estimateFee}(
            address(this),
            destChainId,
            toAddress,
            amount,
            callParams
        );
    }

    function claimAstr(
        address addr,
        uint256 _amount,
        uint256 _minAmount,
        bytes calldata _adapterParams
    ) external payable {
        require(_msgSender() == scriptTrigger, "must be scriptTrigger");
        DerivativeContract(callerToDerivativeAddress[addr]).withdrawNativeToken(
            _amount
        );
        ICommonOFT.LzCallParams memory callParams = ICommonOFT.LzCallParams(
            payable(_msgSender()),
            address(0),
            _adapterParams
        );
        bytes32 toAddress = bytes32(uint256(uint160(addr)));
        (uint256 estimateFee, ) = IOFTWithFee(astrNativeOFT).estimateSendFee(
            destChainId,
            toAddress,
            _amount,
            false,
            _adapterParams
        );
        require(msg.value >= estimateFee, "too small fee");
        if (msg.value != estimateFee) {
            uint256 refundAmount = msg.value - estimateFee;
            (bool success, ) = _msgSender().call{value: refundAmount}("");
            require(success, "failed to refund");
        }
        IOFTWithFee(astrNativeOFT).sendFrom{value: _amount + estimateFee}(
            address(this),
            destChainId,
            toAddress,
            _amount,
            _minAmount,
            callParams
        );
    }

    function setDerivativeAddress(address addr) public {
        require(
            callerToDerivativeAddress[addr] == address(0),
            "already set derivativeAddress"
        );
        bytes memory bytecode = type(DerivativeContract).creationCode;
        bytes32 salt = bytes32(uint256(uint160(addr)));
        address derivativeAddress = Create2.deploy(0, salt, bytecode);
        callerToDerivativeAddress[addr] = derivativeAddress;
        isDerivativeAddress[derivativeAddress] = true;
        emit SetDerivativeAddress(addr, derivativeAddress);
    }

    function setLayerZeroFee() external {
        require(_msgSender() == scriptTrigger, "must be scriptTrigger");
        bytes32 toAddress = bytes32(uint256(uint160(scriptTrigger)));
        uint256 amount = 1000 ether;
        bytes memory adapterParams = abi.encodePacked(
            uint16(1),
            uint256(100000)
        );

        (uint256 vastrFee, ) = IOFTV2(vAstrProxyOFT).estimateSendFee(
            destChainId,
            toAddress,
            amount,
            false,
            adapterParams
        );

        (uint256 astrFee, ) = IOFTWithFee(astrNativeOFT).estimateSendFee(
            destChainId,
            toAddress,
            amount,
            false,
            adapterParams
        );

        astrLayerZeroFee = astrFee;
        vastrLayerZeroFee = vastrFee;

        emit SetLayerZeroFee(
            scriptTrigger,
            astrLayerZeroFee,
            vastrLayerZeroFee
        );
    }

    function setScriptTrigger(address _scriptTrigger) external onlyOwner {
        require(_scriptTrigger != address(0), "invalid address");
        scriptTrigger = _scriptTrigger;
        emit SetScriptTrigger(_scriptTrigger);
    }

    function xcmTransferNativeAsset(address to, uint256 amount) internal {
        bytes32 publicKey = AddressToAccount.AddressToSubstrateAccount(to);
        address[] memory assetId = new address[](1);
        uint256[] memory assetAmount = new uint256[](1);
        assetId[0] = address(0);
        assetAmount[0] = amount;
        require(
            XCM(polkadotXcm).assets_reserve_transfer(
                assetId,
                assetAmount,
                publicKey,
                IS_RELAY_CHAIN,
                BIFROST_PARA_ID,
                0
            ),
            "Failed to send xcm"
        );
    }

    function xcmTransferAsset(
        address assetAddress,
        address to,
        uint256 amount
    ) internal {
        require(assetAddress != address(0), "Invalid assetAddress");
        bytes32 publicKey = AddressToAccount.AddressToSubstrateAccount(to);

        address[] memory assetId = new address[](1);
        uint256[] memory assetAmount = new uint256[](1);
        assetId[0] = assetAddress;
        assetAmount[0] = amount;
        require(
            XCM(polkadotXcm).assets_withdraw(
                assetId,
                assetAmount,
                publicKey,
                IS_RELAY_CHAIN,
                BIFROST_PARA_ID,
                0
            ),
            "Failed to send xcm"
        );
    }

    receive() external payable {
        require(
            isDerivativeAddress[_msgSender()] || _msgSender() == astrNativeOFT,
            "sender is not a derivativeAddress or astrNativeOFT"
        );
        emit Receive(_msgSender(), msg.value);
    }
}

contracts/AstarSlpx.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.10;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "./interfaces/XCM.sol";
import "./interfaces/XCM_v2.sol";
import "./interfaces/ISlpx.sol";
import "./utils/BuildCallData.sol";
import "./utils/AddressToAccount.sol";

contract AstarSlpx is ISlpx, OwnableUpgradeable, PausableUpgradeable {
    address private constant NATIVE_ASSET_ADDRESS =
        0x0000000000000000000000000000000000000000;
    address private constant BNC_ADDRESS =
        0xfFffFffF00000000000000010000000000000007;
    address private constant XCM_ADDRESS =
        0x0000000000000000000000000000000000005004;
    bytes1 private constant ASTAR_CHAIN = 0x00;
    uint32 private constant BIFROST_PARA_ID = 2030;

    enum Operation {
        Mint,
        Redeem,
        ZenlinkSwap,
        StableSwap
    }

    struct AssetInfo {
        bytes2 currencyId;
        uint256 operationalMin;
    }

    struct FeeInfo {
        uint64 transactRequiredWeightAtMost;
        uint256 feeAmount;
    }

    mapping(address => AssetInfo) public addressToAssetInfo;
    mapping(Operation => FeeInfo) public operationToFeeInfo;

    function checkAssetIsExist(
        address assetAddress
    ) internal view returns (bytes2) {
        AssetInfo memory assetInfo = addressToAssetInfo[assetAddress];
        require(assetInfo.operationalMin > 0, "Asset is not exist");
        require(assetInfo.currencyId != bytes2(0), "Invalid asset");
        return assetInfo.currencyId;
    }

    function checkFeeInfo(
        Operation operation
    ) internal view returns (FeeInfo memory) {
        FeeInfo memory feeInfo = operationToFeeInfo[operation];
        require(
            feeInfo.transactRequiredWeightAtMost > 0,
            "Invalid transactRequiredWeightAtMost"
        );
        require(feeInfo.feeAmount > 0, "Invalid feeAmount");
        return feeInfo;
    }

    function initialize() public initializer {
        __Ownable_init();
        __Pausable_init();
        setAssetAddressInfo(NATIVE_ASSET_ADDRESS, 0x0803, 1000000000000000000);
    }

    function setOperationToFeeInfo(
        Operation _operation,
        uint64 _transactRequiredWeightAtMost,
        uint256 _feeAmount
    ) public onlyOwner {
        require(
            _transactRequiredWeightAtMost <= 10000000000,
            "transactRequiredWeightAtMost too large"
        );
        require(_feeAmount <= 1000000000000, "feeAmount too large");
        operationToFeeInfo[_operation] = FeeInfo(
            _transactRequiredWeightAtMost,
            _feeAmount
        );
    }

    function setAssetAddressInfo(
        address assetAddress,
        bytes2 currencyId,
        uint256 minimumValue
    ) public onlyOwner {
        require(minimumValue != 0, "Invalid minimumValue");
        require(currencyId != bytes2(0), "Invalid currencyId");
        AssetInfo storage assetInfo = addressToAssetInfo[assetAddress];
        assetInfo.currencyId = currencyId;
        assetInfo.operationalMin = minimumValue;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function xcmTransferNativeAsset(uint256 amount) internal {
        require(
            amount >= addressToAssetInfo[NATIVE_ASSET_ADDRESS].operationalMin,
            "Less than MinimumValue"
        );
        bytes32 publicKey = AddressToAccount.AddressToSubstrateAccount(
            _msgSender()
        );
        address[] memory assetId = new address[](1);
        uint256[] memory assetAmount = new uint256[](1);
        assetId[0] = NATIVE_ASSET_ADDRESS;
        assetAmount[0] = amount;
        require(
            XCM(XCM_ADDRESS).assets_reserve_transfer(
                assetId,
                assetAmount,
                publicKey,
                false,
                BIFROST_PARA_ID,
                0
            ),
            "Failed to send xcm"
        );
    }

    function xcmTransferAsset(address assetAddress, uint256 amount) internal {
        require(assetAddress != address(0), "Invalid assetAddress");
        require(
            amount >= addressToAssetInfo[assetAddress].operationalMin,
            "Less than MinimumValue"
        );
        bytes32 publicKey = AddressToAccount.AddressToSubstrateAccount(
            _msgSender()
        );

        XCM_v2.Multilocation memory dest_account = getXtokensDestination(
            publicKey
        );
        IERC20 asset = IERC20(assetAddress);
        asset.transferFrom(_msgSender(), address(this), amount);
        require(
            XCM_v2(XCM_ADDRESS).transfer(
                assetAddress,
                amount,
                dest_account,
                XCM_v2.WeightV2(0, 0)
            ),
            "Failed to send xcm"
        );
    }

    function mintVNativeAsset(
        address receiver,
        string memory remark
    ) external payable override whenNotPaused {
        require(bytes(remark).length <= 32, "remark too long");
        bytes2 nativeToken = checkAssetIsExist(NATIVE_ASSET_ADDRESS);

        xcmTransferNativeAsset(msg.value);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildMintCallBytes(
            _msgSender(),
            nativeToken,
            targetChain,
            remark
        );

        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.Mint);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Mint(
            _msgSender(),
            NATIVE_ASSET_ADDRESS,
            msg.value,
            receiver,
            callData,
            remark
        );
    }

    function mintVAsset(
        address assetAddress,
        uint256 amount,
        address receiver,
        string memory remark
    ) external override {
        require(bytes(remark).length <= 32, "remark too long");

        bytes2 token = checkAssetIsExist(assetAddress);

        // xtokens call
        xcmTransferAsset(assetAddress, amount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildMintCallBytes(
            _msgSender(),
            token,
            targetChain,
            remark
        );

        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.Mint);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Mint(
            _msgSender(),
            assetAddress,
            amount,
            receiver,
            callData,
            remark
        );
    }

    function mintVNativeAssetWithChannelId(
        address receiver,
        string memory remark,
        uint32 channel_id
    ) external payable override whenNotPaused {
        require(bytes(remark).length <= 32, "remark too long");
        bytes2 nativeToken = checkAssetIsExist(NATIVE_ASSET_ADDRESS);

        xcmTransferNativeAsset(msg.value);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildMintWithChannelIdCallBytes(
            _msgSender(),
            nativeToken,
            targetChain,
            remark,
            channel_id
        );

        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.Mint);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Mint(
            _msgSender(),
            NATIVE_ASSET_ADDRESS,
            msg.value,
            receiver,
            callData,
            remark
        );
    }

    function mintVAssetWithChannelId(
        address assetAddress,
        uint256 amount,
        address receiver,
        string memory remark,
        uint32 channel_id
    ) external override {
        require(bytes(remark).length <= 32, "remark too long");

        bytes2 token = checkAssetIsExist(assetAddress);

        // xtokens call
        xcmTransferAsset(assetAddress, amount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildMintWithChannelIdCallBytes(
            _msgSender(),
            token,
            targetChain,
            remark,
            channel_id
        );

        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.Mint);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Mint(
            _msgSender(),
            assetAddress,
            amount,
            receiver,
            callData,
            remark
        );
    }

    function redeemAsset(
        address vAssetAddress,
        uint256 amount,
        address receiver
    ) external override whenNotPaused {
        bytes2 vtoken = checkAssetIsExist(vAssetAddress);

        xcmTransferAsset(vAssetAddress, amount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildRedeemCallBytes(
            _msgSender(),
            vtoken,
            targetChain
        );
        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.Redeem);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Redeem(_msgSender(), vAssetAddress, amount, receiver, callData);
    }

    function stablePoolSwap(
        uint32,
        address,
        address,
        uint256,
        uint128,
        address
    ) external pure override {
        require(false, "Not support");
    }

    function swapAssetsForExactAssets(
        address assetInAddress,
        address assetOutAddress,
        uint256 assetInAmount,
        uint128 assetOutMin,
        address receiver
    ) external override whenNotPaused {
        bytes2 assetIn = checkAssetIsExist(assetInAddress);
        bytes2 assetOut = checkAssetIsExist(assetOutAddress);
        require(
            assetIn != bytes2(0) && assetOut != bytes2(0),
            "Invalid currencyId"
        );

        xcmTransferAsset(assetInAddress, assetInAmount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildSwapCallBytes(
            _msgSender(),
            assetIn,
            assetOut,
            assetOutMin,
            targetChain
        );
        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.ZenlinkSwap);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Swap(
            _msgSender(),
            assetInAddress,
            assetOutAddress,
            assetInAmount,
            assetOutMin,
            receiver,
            callData
        );
    }

    function swapAssetsForExactNativeAssets(
        address assetInAddress,
        uint256 assetInAmount,
        uint128 assetOutMin,
        address receiver
    ) external override whenNotPaused {
        bytes2 assetIn = checkAssetIsExist(assetInAddress);
        bytes2 nativeToken = checkAssetIsExist(NATIVE_ASSET_ADDRESS);

        xcmTransferAsset(assetInAddress, assetInAmount);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildSwapCallBytes(
            _msgSender(),
            assetIn,
            nativeToken,
            assetOutMin,
            targetChain
        );
        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.ZenlinkSwap);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Swap(
            _msgSender(),
            assetInAddress,
            NATIVE_ASSET_ADDRESS,
            assetInAmount,
            assetOutMin,
            receiver,
            callData
        );
    }

    function swapNativeAssetsForExactAssets(
        address assetOutAddress,
        uint128 assetOutMin,
        address receiver
    ) external payable override whenNotPaused {
        bytes2 assetOut = checkAssetIsExist(assetOutAddress);
        bytes2 nativeToken = checkAssetIsExist(NATIVE_ASSET_ADDRESS);

        xcmTransferNativeAsset(msg.value);

        bytes memory targetChain = abi.encodePacked(ASTAR_CHAIN, receiver);
        bytes memory callData = BuildCallData.buildSwapCallBytes(
            _msgSender(),
            nativeToken,
            assetOut,
            assetOutMin,
            targetChain
        );
        // xcm transact
        FeeInfo memory feeInfo = checkFeeInfo(Operation.ZenlinkSwap);
        require(
            XCM(XCM_ADDRESS).remote_transact(
                BIFROST_PARA_ID,
                false,
                BNC_ADDRESS,
                feeInfo.feeAmount,
                callData,
                feeInfo.transactRequiredWeightAtMost
            ),
            "Failed to send xcm"
        );
        emit Swap(
            _msgSender(),
            NATIVE_ASSET_ADDRESS,
            assetOutAddress,
            msg.value,
            assetOutMin,
            receiver,
            callData
        );
    }

    function getXtokensDestination(
        bytes32 publicKey
    ) internal pure returns (XCM_v2.Multilocation memory) {
        bytes[] memory interior = new bytes[](2);
        // Parachain: 2001/2030
        interior[0] = bytes.concat(hex"00", bytes4(BIFROST_PARA_ID));
        // AccountId32: { id: public_key , network: any }
        interior[1] = bytes.concat(hex"01", publicKey, hex"00");
        XCM_v2.Multilocation memory dest = XCM_v2.Multilocation({
            parents: 1,
            interior: interior
        });

        return dest;
    }
}

contracts/AstarZkSlpx.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.10;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IOFTWithFee.sol";
import "./interfaces/IOFTV2.sol";
import "./interfaces/Types.sol";

contract AstarZkSlpx is Ownable {
    address public constant astrOFTWithFee =
        0xdf41220C7e322bFEF933D85D01821ad277f90172;
    address public constant vAstrOFT =
        0x7746ef546d562b443AE4B4145541a3b1a3D75717;
    uint16 public constant destChainId = 210;
    bytes32 public remoteContract;

    mapping(Types.Operation => uint256) public minAmount;

    event Mint(address indexed caller, uint256 indexed amount);
    event Redeem(address indexed caller, uint256 indexed amount);

    function setRemoteContract(address _remoteContract) public onlyOwner {
        require(_remoteContract != address(0), "Invalid remoteContract");
        remoteContract = bytes32(uint256(uint160(_remoteContract)));
    }

    function setMinAmount(
        Types.Operation _operation,
        uint256 _minAmount
    ) public onlyOwner {
        require(_minAmount != 0, "Invalid minAmount");
        minAmount[_operation] = _minAmount;
    }

    function mint(
        uint256 _amount,
        uint64 _dstGasForCall,
        bytes calldata _adapterParams
    ) external payable {
        require(_amount >= minAmount[Types.Operation.Mint], "amount too small");
        ICommonOFT.LzCallParams memory callParams = ICommonOFT.LzCallParams(
            payable(_msgSender()),
            address(0),
            _adapterParams
        );

        (uint256 estimateFee, bytes memory payload) = estimateSendAndCallFee(
            _msgSender(),
            Types.Operation.Mint,
            _amount,
            _dstGasForCall,
            _adapterParams
        );
        require(msg.value >= estimateFee, "too small fee");
        if (msg.value != estimateFee) {
            uint256 refundAmount = msg.value - estimateFee;
            (bool success, ) = _msgSender().call{value: refundAmount}("");
            require(success, "failed to refund");
        }

        IOFTWithFee(astrOFTWithFee).sendAndCall{value: estimateFee}(
            _msgSender(),
            destChainId,
            remoteContract,
            _amount,
            _amount / 2,
            payload,
            _dstGasForCall,
            callParams
        );

        emit Mint(_msgSender(), _amount);
    }

    function redeem(
        uint256 _amount,
        uint64 _dstGasForCall,
        bytes calldata _adapterParams
    ) external payable {
        require(
            _amount >= minAmount[Types.Operation.Redeem],
            "amount too small"
        );
        ICommonOFT.LzCallParams memory callParams = ICommonOFT.LzCallParams(
            payable(_msgSender()),
            address(0),
            _adapterParams
        );

        (uint256 estimateFee, bytes memory payload) = estimateSendAndCallFee(
            _msgSender(),
            Types.Operation.Redeem,
            _amount,
            _dstGasForCall,
            _adapterParams
        );
        require(msg.value >= estimateFee, "too small fee");
        if (msg.value != estimateFee) {
            uint256 refundAmount = msg.value - estimateFee;
            (bool success, ) = _msgSender().call{value: refundAmount}("");
            require(success, "failed to refund");
        }

        IOFTV2(vAstrOFT).sendAndCall{value: estimateFee}(
            _msgSender(),
            destChainId,
            remoteContract,
            _amount,
            payload,
            _dstGasForCall,
            callParams
        );

        emit Redeem(_msgSender(), _amount);
    }

    function estimateSendAndCallFee(
        address caller,
        Types.Operation operation,
        uint256 _amount,
        uint64 _dstGasForCall,
        bytes calldata _adapterParams
    ) public view returns (uint256, bytes memory) {
        if (operation == Types.Operation.Mint) {
            bytes memory payload = abi.encode(caller, Types.Operation.Mint);
            (uint256 estimateFee, ) = IOFTWithFee(astrOFTWithFee)
                .estimateSendAndCallFee(
                    destChainId,
                    remoteContract,
                    _amount,
                    payload,
                    _dstGasForCall,
                    false,
                    _adapterParams
                );
            return (estimateFee, payload);
        } else {
            bytes memory payload = abi.encode(caller, Types.Operation.Redeem);
            (uint256 estimateFee, ) = IOFTV2(vAstrOFT).estimateSendAndCallFee(
                destChainId,
                remoteContract,
                _amount,
                payload,
                _dstGasForCall,
                false,
                _adapterParams
            );
            return (estimateFee, payload);
        }
    }
}

contracts/DerivativeContract.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.10;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract DerivativeContract is ReentrancyGuard {
    address public receiver;

    event Withdraw(address caller, address to, address token, uint256 amount);

    constructor() {
        receiver = msg.sender;
    }

    function withdrawErc20Token(
        address _erc20
    ) external nonReentrant returns (uint256) {
        require(msg.sender == receiver, "forbidden");
        require(_erc20 != address(0), "invalid erc20");
        uint256 balance = IERC20(_erc20).balanceOf(address(this));
        require(balance != 0, "balance to low");
        IERC20(_erc20).transfer(receiver, balance);
        emit Withdraw(msg.sender, receiver, _erc20, balance);
        return balance;
    }

    function withdrawNativeToken(uint256 _amount) external nonReentrant {
        require(msg.sender == receiver, "forbidden");
        require(_amount != 0, "balance to low");
        (bool success, ) = receiver.call{value: _amount}("");
        require(success, "failed to withdrawNativeToken");
        emit Withdraw(msg.sender, receiver, address(0), _amount);
    }
}

contracts/EthereumSlpx.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.10;

import "./interfaces/IVETH.sol";
import "./interfaces/snowbridge/IGateway.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/snowbridge/MultiAddress.sol";

contract EthereumSlpx {
    address constant gateway = 0x74bAA141B18D5D1eeF1591abf37167FbeCE23B72;
    address constant slpcore = 0x74bAA141B18D5D1eeF1591abf37167FbeCE23B72;
    address constant veth = 0x4Bc3263Eb5bb2Ef7Ad9aB6FB68be80E43b43801F;
    uint128 constant destinationFee = 1000000;
    uint32 constant paraId = 2030;
    function mint() external payable {
        uint256 fee = IGateway(gateway).quoteSendTokenFee(veth, paraId, destinationFee);
        require(msg.value >= fee, "msg.value to low");
        IVETH(slpcore).mint{value: msg.value - fee}();
        uint256 vethAmount = IERC20(veth).balanceOf(address(this));
        IERC20(veth).approve(gateway, vethAmount);
        IGateway(gateway).sendToken{ value:  fee}(veth, paraId, MultiAddress.MultiAddress({kind: MultiAddress.Kind.Index, data: abi.encode(paraId)}), destinationFee, uint128(vethAmount));
    }
}

